import React, {useState} from 'react'
import { Image, Dimmer, Item} from 'semantic-ui-react'

export default function ImgComp({ src }) {

    // let imgStyles = {
    //     width: 100+'%',
    //     height: 'auto'
    // }

    const [active, setActive] = useState(false)
    const [projectTitle, setProjectTitle] = useState('no info')
    const [projectDes, setProjectDes] = useState('no info')

    const content = (
        <Item>
            <Item.Content>
                <Item.Header as='a'>projectTitle</Item.Header>
                <Item.Meta>Description</Item.Meta>
                <Item.Description>
                    {projectDes}
                </Item.Description>
                <Item.Extra>Additional Details</Item.Extra>
            </Item.Content>
        </Item>
      )

    // const handleClick = (evt) => {
    //     if(evt.target.src.includes('therappoint')){
    //         window.open('https://www.youtube.com/watch?v=QZEsGHZpaoo&feature=youtu.be')
    //     }else if(evt.target.src.includes('nyc')){
    //         window.open('https://www.youtube.com/watch?v=1PeeuhozAIo&feature=youtu.be')
    //     }else if(evt.target.src.includes('shop')){
    //         window.open('https://www.youtube.com/watch?v=7cNyoHjJjiw&feature=youtu.be')
    //     }else if(evt.target.src.includes('dict')){
    //         window.open('https://github.com/GAierken/SlangTrans')
    //     }
    // }

    const handleShow = () => {
        setActive(true)
        if(src.includes('therappoint')){
            setProjectTitle('Therappoint')
            setProjectDes('Appointment scheduling app developed especially for early intervention service agency\-Implemented frontend with React (Redux, Thunk, Datepicker, Google Map, Geocode)\-Utilized JSON Web Tokens and localStorage to store encrypted user information client-side\-Styled with Semantic UI and custom CSS to refine client-side interaction and user experience\-Built a Ruby on Rails API backend powered by PostgreSQL using self referential relationship')
        }else if(src.includes('nyc')){
            setProjectTitle('Museums in NYC')
        }else if(src.includes('shop')){
            setProjectTitle('Flatiron Baby Shopping')
        }else if(src.includes('dict')){
            setProjectTitle('SlangTrans')
        }
    }

    const handleHide = () => {
        setActive(false)
    }


    return (
        <React.Fragment>
            {/* <Image className='projects carousel'  onClick={handleClick} src={src} alt='slide-img' style={imgStyles}/> */}
            <Dimmer.Dimmable
                as={Image}
                dimmed={active}
                dimmer={{ active, content }}
                onMouseEnter={handleShow}
                onMouseLeave={handleHide}
                src={src}

            />
        </React.Fragment>
    )
}
