import React, { useState } from 'react';
import './App.css';
import Home from './Components/Home'
import Contacts from './Components/Contacts'
import Menu from './Components/Menu'
import { Checkbox, Label, Container, Image } from 'semantic-ui-react';
import { Switch, Route } from "react-router-dom";
import About from './Components/About'
import Projects from './Components/Projects'
import Banner from './banner_pic.jpg'

function App() {
  const [darkMode, setDarkMode] = useState(false)

  const handleOnChange = () => {
      setDarkMode(!darkMode)
      changeBackground()
  }

  const changeBackground = () => {
    var element = document.body;
      element.classList.toggle("dark-mode")
  }

  return (

    <React.Fragment>
      <Image style={{width: '100%', height:'400px'}}src={Banner}></Image>

        <Label size="large" style={{position: 'absolute', top: 0,
          right: '0px', 'font-family': "'Montserrat', sans-serif"}}><Checkbox toggle onChange={handleOnChange} />Night mode</Label>
        <Menu/>
        <Contacts/>
      <Switch>
        <Route path='/about'><About/></Route>
        <Route path='/projects'><Projects/></Route>
        <Route path='/'><Home/></Route>
      </Switch>
      <Container style={{'bottom': '0', color: 'navy', 'font-family': "'Montserrat', sans-serif"}} textAlign='center'>Created by Guligena Aierken</Container>
    </React.Fragment>


  );
}

export default App;
