import React from 'react'
import { useHistory } from 'react-router-dom'
import {Sidebar, Menu, Icon} from 'semantic-ui-react'

export default function MenuSidebar(props) {
    let history = useHistory()

    const handleClick = (evt) => {
      console.log(evt.target.dataset)
        if(evt.target.childNodes[1] === 'Home'){
          history.push('/')
        }else if(evt.target.childNodes[1] === 'About Me'){
          history.push('/about')
        }else if(evt.target.childNodes[1] === 'Projects'){
          history.push('/projects')
        }

      }
    return (
        <Sidebar
        as={Menu}
        color='blue'
        animation='overlay'
        icon='labeled'
        inverted
        vertical
        visible={props.visible}
        width='very thin'
        direction='bottom'
      >
        <Menu.Item data-id='1' as='a' onClick={handleClick}>
          <Icon name='home' />
          Home
        </Menu.Item>
        <Menu.Item as='a' onClick={handleClick}>
          <Icon name='heart'/>
          About Me
        </Menu.Item >
        <Menu.Item as='a' onClick={handleClick}>
          <Icon name='folder'/>
          Projects
        </Menu.Item>
        <Menu.Item as='a' onClick={handleClick}>
          <Icon name='mail'/>
          Contact Me
        </Menu.Item>
      </Sidebar>)
}
